import axios from "axios";
import React, { useEffect, useState } from "react";
import Header from "../header";
import SubHeader from "../subHeader";
import DoctorSection from "./doctorSection";
import Modal from "react-modal";
import Checkbox from "../../shared/checkbox";

export default function Consult() {

  const customStyles = {
    content: {
      top: "50%",
      left: "50%",
      right: "auto",
      bottom: "auto",
      marginRight: "-50%",
      transform: "translate(-50%, -50%)",
    },
  };

  const [selected, setSelected] = useState([]);
  const [configuration, setConfiguration] = useState([]);
  const [med, setMed] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [modalCheckedData, setModalCheckedData] = useState({
    medicine : false,
    vitals: false,
    advice: false,
    diagnosis: false
  })

  const handleGetMedicines = () => {
    axios
      .get("https://myplusdoctor.med2myhome.com/api/getmedicines?name=")
      .then((res) => {
        setMed(res.data.data.medicines);
        // console.log(res.data.data.medicines);
      });
  };

  const getConfigurations = () => {
    axios
      .get("https://myplusdoctor.med2myhome.com/api/configuration")
      .then((res) => {
        setConfiguration(res.data);
      })
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    getConfigurations();
    handleGetMedicines();
  }, []);

  const handleMedicineSearch = (val) => {
    if (val.length > 3) {
      axios
        .get(`https://myplusdoctor.med2myhome.com/api/getmedicines?name=${val}`)
        .then((res) => {
          setMed(res.data.data.medicines);
        });
    }
  };

  const handleMedChange = (data) => {
    console.log(data);
    setSelected([...data]);
  };

  const handleCompleteAppointment = () => {
    setShowModal(true);
  };


  const handleModalCheckboxData = (e) => {
    const { name, checked } = e.target
    const data = {
      ...modalCheckedData,
      [name.toLowercase()] : checked 
    }
    console.log(data)
  }



  return (
    <>
      <div>
        <Header />
        <SubHeader
          label={"Doctor consultation"}
          completeBtn={{
            label: "Complete Consultation",
            onclick: handleCompleteAppointment,
          }}
        />
        <div className="consult-content">
          <div className="video-content">
            <div className="video"></div>
          </div>
          <div className="doctor-work-sec">
            <div className="section-container">
              <DoctorSection
                title={"Medicine"}
                onsearch={handleMedicineSearch}
                onchange={handleMedChange}
                options={med}
                selected={selected}
                configuration={configuration}
              />
              <DoctorSection
                title={"Diagnosis"}
                onsearch={handleMedicineSearch}
                onchange={handleMedChange}
                options={med}
                selected={selected}
                configuration={configuration}
              />
              <DoctorSection
                title={"tests"}
                onsearch={handleMedicineSearch}
                onchange={handleMedChange}
                options={med}
                selected={selected}
                configuration={configuration}
              />
            </div>
          </div>
        </div>
      </div>

      <Modal
        isOpen={true}
        // onAfterOpen={afterOpenModal}
        // onRequestClose={closeModal}
        // style={customStyles}
        // contentLabel="Example Modal"
        className="custom-modal-content"
      >
        <div className="header d-flex justify-content-between">
          <div className="title">Complete Prescription</div>
          <div className="close">X</div>
        </div>
        <div className="body-content mt-3 ">
          <Checkbox
            label={"Send Patinet Vitals"}
            onchange={handleModalCheckboxData}
          />
          <Checkbox label={"Send Medicine"} onchange={handleModalCheckboxData} />
          <Checkbox label={"Send Advice"} onchange={handleModalCheckboxData} />
          <Checkbox label={"Send Diagnosis"} onchange={handleModalCheckboxData} />
        </div>
      </Modal>
    </>
  );
}
