import React from "react";

import Multiselect from "multiselect-react-dropdown";

export default function DoctorSection({
  title,
  options,
  onchange,
  onsearch,
  selected,
  configuration,
  isMedSec = false,
  loading
}) {

  const customCssMulti = {
    chips: {
      background : '#d24350'
    },
    option: {
      color: '#d24350'
    }
  }

  return (
    <div className="doc-sec" >
      <div className="complete-section">
        <div className="textarea-sec">
          <div className="section-title">
            {title}
            <span>.</span>
          </div>
          <div className="textarea">
            <textarea
              placeholder={`Enter ${title} notes...`}
              className="h-100 w-100 border-0 "
            ></textarea>
          </div>
        </div>
        <div className="multi-dropdown">
          <Multiselect
            options={options}
            onSelect={onchange}
            onRemove={onchange}
            onSearch={onsearch}
            displayValue="name"
            loading={loading}
            placeholder={`Select ${title}`}
            style={customCssMulti}
          />
        </div>
        <div className="dropdown-sec">
          {isMedSec && (
            <div>
              {selected.map((item) => (
                <div className="addon-sec" key={item.value}>
                  <div className="d-flex flex-column text">
                    <label>Name</label>
                    <input className="input" value={item.name} />
                  </div>
                  <div className="time d-flex flex-column ">
                    <label for="dosage">Dosage</label>
                    <div>
                      <select name="cars" id="dosage">
                        {configuration.medicine_dosage.map((dosage) => (
                          <option value={dosage.id}>{dosage.name}</option>
                        ))}
                      </select>
                    </div>
                  </div>
                  <div className="time d-flex flex-column ">
                    <label for="frequency">Frequency</label>
                    <div>
                      <select name="frequency" id="frequency">
                        {configuration.medicine_frequency.map((freq) => (
                          <option value={freq.id}>{freq.name}</option>
                        ))}
                      </select>
                    </div>
                  </div>
                  <div className="time d-flex flex-column ">
                    <label for="time">Time</label>
                    <div>
                      <select name="time" id="time">
                        {configuration.medicine_time.map((time) => (
                          <option value={time.id}>{time.name}</option>
                        ))}
                      </select>
                    </div>
                  </div>
                  <div className="time d-flex flex-column ">
                    <label for="is_before_food">When</label>
                    <div>
                      <select name="is_before_food" id="is_before_food">
                        <option value="1">Before food</option>
                        <option value="0">After food</option>
                      </select>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
