import axios from "axios";
import React, { useEffect, useState } from "react";
import Header from "../header";
import SubHeader from "../subHeader";
import DoctorSection from "./doctorSection";
import Modal from "react-modal";

export default function Consult() {
  // useEffect(()=>{
  //     axios.defaults.headers.common['Authorization'] = "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiJ9.eyJhdWQiOiIxIiwianRpIjoiNDlhZjMwNzI3MmVlMWZkZThkOTEzNTUwNGRhYzVjNWQxZWYzYjFhNWUzODhmNWEwMTdlYTQxMzM4ZTI1NGYyNGYzMjY4ZTIyNTMyOWIyMGMiLCJpYXQiOjE2MjkzNDgzMDksIm5iZiI6MTYyOTM0ODMwOSwiZXhwIjoxNjYwODg0MzA5LCJzdWIiOiIxIiwic2NvcGVzIjpbInVzZXIiXX0.2Z-P3JngQG8eu0ct36ML33AShOn3uLcCshGw3sD2PQXkXpg_Tp0nH2BKocBKZARIaurDCRrnAE5_oR5pul3HxAv4nUmc7ACqH8IakcalTaFlNv5Iz7ZHe8DWbSZ_99mxhtm-EG_5uwLaeZbUXXdBelmJ6TYS0lQVlpRllv1UEKSgO_ViEG-aPFltmS1AWvSpNZcuvKbRfhVAerOsIfFDQqCnbov5v-NTVFSa_qVhPtrs5Fc0j0Rmz0FtonshrYusvTS07HEAU2_HmM8P1D2DhDWSLYSRBX9YM2OG6rQylWGClXt1eI0bRaINUn2-h1I8x6FIQSEz8pjKcY1b9vYKjtoPa1nl6iKYqEZlp5B8YZruDblLwG_JTNQV-mtpCNBf9GdNrlTeZHyqz7NqkW-5SxqaoixyUIzZZPOKqaKQfItR86aBNI00ydSC0Ew-V3GYBjkgV204Zs1bqIwO_YEAZNP6Tr6vb0SYzsdsFwtnkDTFVfcD7TRNKc0mLmL8t0whgJcSGb5b9hL1x127GaJVQO4OHCWqBqHDfSDPq0h3ZbGKn9_QdVWSrqxkQCveCl6TELm7nQP82I7Ze8vEHQcXgIErWYvvnHyWtzCWVsqwIjQRO88EIbMmLBZVyZZvfn0Cfr9DlutYH5ciZPrsjmHOa7AvWrAzRcQADgoysOykNYo";
  //     axios.post("https://myplusdoctor.med2myhome.com/api/doctor/startdoctorconsult?doctor_id=1")
  //     .then(res=>console.log(res.data))
  // },[])

  // <div className="iframe-body">
  //     <iframe className="w-100 h-100" allow="camera;microphone" src="https://meet.jit.si/myplusdoctor738bf52044#userInfo.displayName=%22hansraj%22&userInfo.email=%22"></iframe>
  // </div>

  const [selected, setSelected] = useState([]);
  const [configuration, setConfiguration] = useState([]);
  const [med, setMed] = useState([]);
  const [showModal, setShowModal] = useState(false)

  const handleGetMedicines = () => {
    axios
      .get("https://myplusdoctor.med2myhome.com/api/getmedicines?name=")
      .then((res) => {
        setMed(res.data.data.medicines);
        console.log(res.data.data.medicines);
      });
  };

  const getConfigurations = () => {
    axios
      .get("https://myplusdoctor.med2myhome.com/api/configuration")
      .then((res) => {
        setConfiguration(res.data);
      })
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    getConfigurations();
    handleGetMedicines();
  }, []);

  const handleMedicineSearch = (val) => {
    if (val.length > 3) {
      axios
        .get(`https://myplusdoctor.med2myhome.com/api/getmedicines?name=${val}`)
        .then((res) => {
          setMed(res.data.data.medicines);
        });
    }
  };

  const handleMedChange = (data) => {
    console.log(data);
    setSelected([...data]);
  };

  const handleCompleteAppointment = () => {
      setShowModal(true)
  };

  return (
    <>
      <div>
        <Header />
        <SubHeader
          label={"Doctor consultation"}
          completeBtn={{
            label: "Complete Consultation",
            onclick: handleCompleteAppointment,
          }}
        />
        <div className="consult-content">
          <div className="video-content">
            <div className="video"></div>
          </div>
          <div className="doctor-work-sec">
            <div className="section-container">
              <DoctorSection
                title={"Medicine"}
                onsearch={handleMedicineSearch}
                onchange={handleMedChange}
                options={med}
                selected={selected}
                configuration={configuration}
              />
              <DoctorSection
                title={"Diagnosis"}
                onsearch={handleMedicineSearch}
                onchange={handleMedChange}
                options={med}
                selected={selected}
                configuration={configuration}
              />
              <DoctorSection
                title={"tests"}
                onsearch={handleMedicineSearch}
                onchange={handleMedChange}
                options={med}
                selected={selected}
                configuration={configuration}
              />
            </div>
          </div>
        </div>
      </div>

      <Modal
        isOpen={showModal}
        // onAfterOpen={afterOpenModal}
        // onRequestClose={closeModal}
        // style={customStyles}
        contentLabel="Example Modal"
      >
        <div className="modal-content">
          <div className="checkbox" >
            <input type="checkbox"  />
            <label>for check</label>
          </div>
        </div>
      </Modal>
    </>
  );
}
