import axios from "axios";
import React, { useEffect, useState } from "react";
import Header from "../header";
import SubHeader from "../subHeader";
import DoctorSection from "./doctorSection";
import Modal from "react-modal";
import Checkbox from "../../shared/checkbox";

export default function Consult() {
  const customStyles = {
    content: {
      top: "50%",
      left: "50%",
      right: "auto",
      bottom: "auto",
      marginRight: "-50%",
      transform: "translate(-50%, -50%)",
    },
  };

  const [selected, setSelected] = useState([]);
  const [configuration, setConfiguration] = useState([]);
  const [med, setMed] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [modalCheckedData, setModalCheckedData] = useState({
    medicine: false,
    vitals: false,
    advice: false,
    diagnosis: false,
  });
  const [isMedLoading, setIsMedLoading] = useState(false)

  const handleGetMedicines = () => {
    axios
      .get("https://myplusdoctor.med2myhome.com/api/getmedicines?name=")
      .then((res) => {
        setMed(res.data.data.medicines);
        // console.log(res.data.data.medicines);
      });
  };

  const getConfigurations = () => {
    axios
      .get("https://myplusdoctor.med2myhome.com/api/configuration")
      .then((res) => {
        setConfiguration(res.data);
      })
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    getConfigurations();
    handleGetMedicines();
  }, []);

  const handleMedicineSearch = (val) => {
    setIsMedLoading(true)
    if (val.length > 3) {
      axios
        .get(`https://myplusdoctor.med2myhome.com/api/getmedicines?name=${val}`)
        .then((res) => {
          setMed(res.data.data.medicines);
          setIsMedLoading(false)
        });
    }
  };

  const handleMedChange = (data) => {
    setSelected([...data]);
  };

  const handleCompleteAppointment = () => {
    setShowModal(true);
  };

  const handleModalCheckboxData = (e) => {
    const { name, checked } = e.target;
    const data = {
      ...modalCheckedData,
      [name.toLowerCase()]: checked,
    };
    setModalCheckedData(data);
  };


  return (
    <>
      <div>
        <Header />
        <SubHeader
          label={"Doctor consultation"}
          completeBtn={{
            label: "Complete Consultation",
            onclick: handleCompleteAppointment,
          }}
        />
        <div className="consult-content">
          <div className="video-content">
            <div className="video"></div>
          </div>
          <div className="doctor-work-sec">
            <div className="section-container">
              <DoctorSection
                title={"Medicine"}
                onsearch={handleMedicineSearch}
                onchange={handleMedChange}
                options={med}
                selected={selected}
                configuration={configuration}
                // isMedSec={true}
                loading={isMedLoading}
              />
              <DoctorSection
                title={"Diagnosis"}
                onsearch={handleMedicineSearch}
                onchange={handleMedChange}
                options={med}
                selected={selected}
                configuration={configuration}
              />
              <DoctorSection
                title={"Tests"}
                onsearch={handleMedicineSearch}
                onchange={handleMedChange}
                options={med}
                selected={selected}
                configuration={configuration}
              />
              <DoctorSection
                title={"Common Notes"}
                onsearch={handleMedicineSearch}
                onchange={handleMedChange}
                options={false}
                selected={selected}
                configuration={configuration}
              />
            </div>
            {selected.length ? (
              <div className="selectedMed">
                <div className="dropdown-sec">
                  {isMedSec && (
                    <div>
                      {selected.map((item) => (
                        <div className="addon-sec" key={item.value}>
                          <div className="d-flex flex-column text">
                            <label>Name</label>
                            <input className="input" value={item.name} />
                          </div>
                          <div className="time d-flex flex-column ">
                            <label for="dosage">Dosage</label>
                            <div>
                              <select name="cars" id="dosage">
                                {configuration.medicine_dosage.map((dosage) => (
                                  <option value={dosage.id}>
                                    {dosage.name}
                                  </option>
                                ))}
                              </select>
                            </div>
                          </div>
                          <div className="time d-flex flex-column ">
                            <label for="frequency">Frequency</label>
                            <div>
                              <select name="frequency" id="frequency">
                                {configuration.medicine_frequency.map(
                                  (freq) => (
                                    <option value={freq.id}>{freq.name}</option>
                                  )
                                )}
                              </select>
                            </div>
                          </div>
                          <div className="time d-flex flex-column ">
                            <label for="time">Time</label>
                            <div>
                              <select name="time" id="time">
                                {configuration.medicine_time.map((time) => (
                                  <option value={time.id}>{time.name}</option>
                                ))}
                              </select>
                            </div>
                          </div>
                          <div className="time d-flex flex-column ">
                            <label for="is_before_food">When</label>
                            <div>
                              <select name="is_before_food" id="is_before_food">
                                <option value="1">Before food</option>
                                <option value="0">After food</option>
                              </select>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            ) : null}
          </div>
        </div>
      </div>

      <Modal
        isOpen={showModal}
        // onAfterOpen={afterOpenModal}
        // onRequestClose={closeModal}
        // style={customStyles}
        // contentLabel="Example Modal"
        className="custom-modal-content"
      >
        <div className="header d-flex justify-content-between">
          <div className="title">Complete Prescription</div>
          <div className="close" onClick={() => setShowModal(false)}>
            X
          </div>
        </div>
        <div className="body-content mt-3 ">
          <Checkbox
            label={"Send Patinet Vitals"}
            onchange={handleModalCheckboxData}
          />
          <Checkbox
            label={"Send Medicine"}
            onchange={handleModalCheckboxData}
          />
          <Checkbox label={"Send Advice"} onchange={handleModalCheckboxData} />
          <Checkbox
            label={"Send Diagnosis"}
            onchange={handleModalCheckboxData}
          />
        </div>
        <div className="footer-modal d-flex justify-content-end ">
          <div
            className="btn btn btn-outline-secondary me-3"
            onClick={() => setShowModal(false)}
          >
            Close
          </div>
          <div className="btn btn-primary">Send prescription</div>
        </div>
      </Modal>
    </>
  );
}
