                  <input type="text" class="form-control" ref={mbNoRef} />
                </div>
              </div>
            </div>

            <div className="col-6">
              <div class="mb-3  row">
                <label class="col-sm-4 col-form-label pe-0">Email :</label>
                <div class="col-sm-8">
                  <input type="text" class="form-control" ref={emailRef} />
                </div>
              </div>
            </div>
            {/* <div className="col-6">
                                    <label>If the patient is as family memver</label>
                                    <input type="radio" />
                                </div> */}
            <div className="col-6 d-flex mb-3">
              <div className="col-form-label col-sm-4">Relation :</div>
              <select
                class="form-select h-75 ms-2"
                ref={relationRef}
                aria-label="Default select example"
              >
                <option selected>Select</option>
                {confirgureData?.relation?.map((itr) => (
                  <option value={itr.id}>{itr.name}</option>
                ))}
              </select>
            </div>
            <div className="col-6">
              <div class="mb-3  row">
                <label for="inputPassword" class="col-sm-4 col-form-label">
                  Aadhaar No :
                </label>
                <div class="col-sm-8">
                  <input type="text" class="form-control" ref={aadhaarRef} />
                </div>
              </div>
            </div>
            <div className="col-6">
              <div class="mb-3  row">
                <label for="inputPassword" class="col-sm-4 col-form-label">
                  Address :
                </label>
                <div class="col-sm-8">
                  <textarea type="text" class="form-control" ref={addressRef} />
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="modal-footer border-0">
          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => setIsAddModal(false)}
          >
            Close
          </button>
          <button
            type="button"
            className="btn btn-primary"
            onClick={handleSubmit}
          >
            Save changes
          </button>
        </div>
      </Modal>
      {/* </div>
                </div>
            </div> */}
      {/* } */}
    </>
  );
